import React, { useEffect, useState } from "react";
import Table from "@mui/material/Table";
import TableHead from "@mui/material/TableHead";
import TableBody from "@mui/material/TableBody";
import TableRow from "@mui/material/TableRow";
import TableCell,{ tableCellClasses }  from "@mui/material/TableCell";
import Button from "@mui/material/Button";
import { useNavigate } from "react-router-dom";
import {styled} from '@mui/material/styles';
import axios from "axios";
import Paper from '@mui/material/Paper';
import TableContainer from '@mui/material/TableContainer';

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));


const Services = ({ user, setUser }) => {
  const navigate = useNavigate();

  const [items, setItems] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/items").then((res) => {
      setItems(res.data);
    });
  }, []);

  const onClickButton = (itemId) => {
    navigate("/complete", { state: { id: itemId } });
  };
  return (
    <TableContainer component={Paper}>
      <Table className="shadowStyle">
        <TableHead>
          <TableRow>
            <TableCell className="TableCellStyle" bgcolor="#3949ab" >유저 id</TableCell>
            <TableCell className="TableCellStyle" bgcolor="#3949ab">남은 크레딧</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          <TableRow>
            <TableCell className="TableCellStyle">{user.username}</TableCell>
            <TableCell className="TableCellStyle">{user.credit}</TableCell>
          </TableRow>
        </TableBody>
      </Table>

      <Table className="shadowStyle">
        <TableHead>
          <TableRow>
            <TableCell className="TableCellStyle">서비스 id</TableCell>
            <TableCell className="TableCellStyle">서비스명</TableCell>
            <TableCell className="TableCellStyle">가격</TableCell>
            <TableCell  className="TableCellStyle">신청버튼</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {items.map((item, index) => (
            <TableRow>
              <TableCell className="TableCellStyle">{item.id}</TableCell>
              <TableCell className="TableCellStyle">{item.itemName}</TableCell>
              <TableCell className="TableCellStyle">{item.price}크레딧</TableCell>
              <TableCell className="TableCellStyle">
                <Button
                  variant="contained"
                  onClick={() => onClickButton(item.id)}
                >
                  신청
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};
export default Services;
